package Basic;

public class alternateEvenNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10;i++)
		{
//			System.out.println("W "+i);
			if(i%2==0)
			{
				System.out.println("Alternate even num "+i);
				i+=2;
//				System.out.println("L "+i);
			}	
		}

	}

}
