package OOPS;

public class basic {
	static String name; 
	static String color;
	static String brand;
	static int price;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		basic c1 = new basic();
		basic.name = "i8";
		c1.color = "Black";
		c1.brand = "BMW";
		c1.price = 20000000;
		System.out.println(name);
		System.out.println(color);
		System.out.println(brand);
		System.out.println(price);
		
	}

}
