package conditionalStatements;

public class switchPgmVowel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'i';
		switch(ch)
		{
		case 'a':
		{
			System.out.println(ch + " is a Vowel");
		}
		break;
		case 'e':
		{
			System.out.println(ch + " is a Vowel");
		}
		break;
		case 'i':
		{
			System.out.println(ch + " is a Vowel");
		}
		break;
		case 'o':
		{
			System.out.println(ch + " is a Vowel");
		}
		break;
		case 'u':
		{
			System.out.println(ch + " is a Vowel");
		}
		break;
		default :
		{
			System.out.println("Not a Vowel");
		}
		}

	}

}
